');\\n\\nconst express = require('express');\\nconst { Pool } = require('pg');\\nconst { trace } = require('@opentelemetry/api');\\n\\nconst app = express();\\nconst PORT = process.env.PORT || 8082;\\n\\napp.use(express.json());\\n\\nconst pool = new Pool({\\n  host: process.env.DB_HOST || 'postgres',\\n  port: 5432,\\n  database: 'ecommerce',\\n  user: 'appuser',\\n  password: 'apppass',\\n  max: 5,\\n  connectionTimeoutMillis: 5000,\\n  idleTimeoutMillis: 30000\\n});\\n\\npool.on('error', (err) => {\\n  console.error('Unexpected database error:', err);\\n});\\n\\npool.on('connect', () => {\\n  console.log('New database connection established');\\n});\\n\\napp.post('/api/payments/process', async (req, res) => {\\n  const tracer = trace.getTracer('payment-service');\\n  const span = tracer.startSpan('processPayment');\\n  \\n  const { userId, productId, amount } = req.body;\\n  \\n  span.setAttribute('user.id', userId);\\n  span.setAttribute('product.id', productId);\\n  span.setAttribute('payment.amount', amount || 0);\\n  \\n  let client;\\n  \\n  try {\\n    span.addEvent('acquiring_db_connection');\\n    client = await pool.connect();\\n    span.addEvent('db_connection_acquired');\\n    \\n    span.setAttribute('db.pool.size', pool.totalCount);\\n    span.setAttribute('db.pool.idle', pool.idleCount);\\n    span.setAttribute('db.pool.waiting', pool.waitingCount);\\n    \\n    // Simulate slow payment processing\\n    await client.query('SELECT pg_sleep(0.5)');\\n    \\n    const result = await client.query(\\n      'INSERT INTO payments (user_id, product_id, amount, status) VALUES ($1, $2, $3, $4) RETURNING id',\\n      [userId, productId || 1, amount || 99.99, 'completed']\\n    );\\n    \\n    span.setAttribute('payment.id', result.rows[0].id);\\n    span.setAttribute('payment.status', 'success');\\n    \\n    res.json({ \\n      success: true, \\n      paymentId: result.rows[0].id,\\n      status: 'completed'\\n    });\\n    \\n  } catch (err) {\\n    span.recordException(err);\\n    span.setAttribute('error', true);\\n    span.setAttribute('error.type', err.code || 'unknown');\\n    \\n    console.error('Payment processing error:', err.message);\\n    \\n    res.status(500).json({ \\n      success: false, \\n      error: 'Payment processing failed',\\n      details: err.message\\n    });\\n  } finally {\\n    // ALWAYS release connection, regardless of success or failure\\n    // This prevents connection pool exhaustion\\n    if (client) {\\n      client.release();\\n      span.addEvent('connection_released');\\n    }\\n    span.end();\\n  }\\n});\\n\\napp.get('/api/payments/health', async (req, res) => {\\n  try {\\n    const result = await pool.query('SELECT 1');\\n    res.json({ \\n      status: 'healthy',\\n      database: 'connected',\\n      pool: {\\n        total: pool.totalCount,\\n        idle: pool.idleCount,\\n        waiting: pool.waitingCount\\n      }\\n    });\\n  } catch (err) {\\n    res.status(503).json({ \\n      status: 'unhealthy', \\n      database: 'disconnected',\\n      error: err.message\\n    });\\n  }\\n});\\n\\napp.get('/health', (req, res) => {\\n  res.json({ status: 'healthy', service: 'payment-service' });\\n});\\n\\nconst server = app.listen(PORT, () => {\\n  console.log(`Payment Service listening on port ${PORT}`);\\n});\\n\\nprocess.on('SIGTERM', () => {\\n  console.log('SIGTERM received, shutting down gracefully...');\\n  server.close(() => {\\n    pool.end(() => {\\n      console.log('Pool has ended');\\n      process.exit(0);\\n    });\\n  });\\n});\\n", "root_cause_summary": "Commit 7d6f976c introduced a bug that conditionally releases database connections only when 'amount != null'. This causes connection leaks for any payment request without an explicit amount, eventually exhausting the connection pool (max: 5 connections). The fix restores the original pattern of releasing connections in the finally block, ensuring cleanup happens for ALL requests."}, "name": "github_fix", "typ